import { BsCheckSquareFill, BsSquare, BsTrash3Fill } from "react-icons/bs";
import { MdEdit } from 'react-icons/md';

const Item = ({ task, id, handleDeleteTask, handleIsDone, isDone, handleEditTask, isEdit }) => {
    return (
        <div>
            <button className='emoji' onClick={() => handleIsDone(id)}>{ isDone ? <BsCheckSquareFill /> : <BsSquare />}</button>
            <span className={ isDone ? 'complete' : 'incomplete'}>{ isEdit? <input type='text' value={task} /> : task }</span>
            <div className='wrapper'>
                <button className='emoji edit-icon' onClick={() => handleIsEdit(id)}><MdEdit /></button>
                <button className='emoji trash-icon' onClick={() => handleDeleteTask(id)}><BsTrash3Fill /></button>
            </div>
        </div>
    );
}

export default Item;
